import * as React from 'react'
import './styles.module.css'

export interface Item {
  label: string
  value: number
}

export interface Cell {
  id: number
  item1: Item
  item2: Item
}
export interface Row {
  label: string
  data: Cell[]
}

interface Props {
  rows: Row[]
  color1: string
  color2: string
  modifier: string
}

export const DuoHeatmap: React.FC<Props> = (props) => {
  const { modifier, rows } = props
  return (
    <div
      className={`duo-heatmap ${modifier ? 'duo-heatmap--' + modifier : ''}`}
    >
      {rows.map((row, index) => (
        <div key={row.label + index} className={`row row--${index}`}>
          <div className='row__label'>{row.label}</div>
          <div className='row__data'>
            {row.data.map((cell) => {
              return (
                <div key={cell.id} className='cell'>
                  <div className='cell__data'>
                    <div className='cell__item'>
                      <span className='cell__item--label'>
                        {cell.item1.label}
                      </span>
                      <span className='cell__item--value'>
                        {cell.item1.value}
                      </span>
                    </div>
                    <div className='cell__item'>
                      <span className='cell__item--label'>
                        {cell.item2.label}
                      </span>
                      <span className='cell__item--value'>
                        {cell.item2.value}
                      </span>
                    </div>
                  </div>
                </div>
              )
            })}
          </div>
        </div>
      ))}
    </div>
  )
}
