import React from 'react'
import styles from './row.module.scss'

interface RowProps {
  label?: string
  index: number
}

const Row: React.FC<RowProps> = (props) => {
  const { label, children } = props

  return (
    <div className={styles.row}>
      {label ? <div className={styles.rowLabel}>{label}</div> : null}
      <div className={styles.rowData}>{children}</div>
    </div>
  )
}

export default Row
import React from 'react'
import styles from './cell.module.scss'

export interface Item {
  label: string
  value: number | undefined | null
}

export interface RGB {
  red: number
  green: number
  blue: number
}

export interface Props {
  colors: RGB[]
  highestValue: number
  item1: Item
  item2: Item
}

const Cell: React.FC<Props> = (props) => {
  const { item1, item2, colors, highestValue } = props
  const stepAmount = colors.length

  // values can be null or undefined
  const value1 = item1.value || 0
  const value2 = item2.value || 0

  //  maximum value of the items values
  const cellMaxValue = value1 + value2

  // ratio of the first value inverted
  const ratio = 1 - value1 / cellMaxValue

  // calculated index of the color lerp
  let colorIndex = Math.round(ratio * stepAmount) - 1

  // sometimes the index is -1 and then it must be 0 or there will be pain
  if (colorIndex < 0) {
    colorIndex = 0
  }

  // alpha value of the total highest value of the given range
  const alpha = 1 / (highestValue / cellMaxValue)
  const cellColor = cellMaxValue
    ? colors[colorIndex]
    : { red: 255, green: 255, blue: 255 }

  return (
    <div
      className={styles.cell}
      style={{
        backgroundColor: `rgba(${cellColor.red}, ${cellColor.green}, ${
          cellColor.blue
        }, ${cellMaxValue ? alpha : 1})`
      }}
    >
      {cellMaxValue ? (
        <div className={styles.cellData}>
          <div className={styles.cellItem}>
            <span className={styles.cellItemLabel}>{item1.label}</span>
            <span className={styles.cellItemValue}>{value1}</span>
          </div>
          <div className={styles.cellItem}>
            <span className={styles.cellItemLabel}>{item2.label}</span>
            <span className={styles.cellItemValue}>{value2}</span>
          </div>
        </div>
      ) : null}
    </div>
  )
}

export default Cell
import * as React from 'react'
import Row from './components/row/row-component'
import Cell, { Item } from './components/cell/cell-component'

interface DuoHeatmapProps {
  rows: IRow[]
  color1?: string
  color2?: string
  steps?: number
  modifier?: string
}

export interface IRow {
  label: string
  data: ICell[]
}

export interface ICell {
  id: number
  item1: Item
  item2: Item
}
const DuoHeatmap: React.FC<DuoHeatmapProps> = (props) => {
  const { modifier, rows, color1, color2, steps } = props
  const [highestValue, setHighestValue] = React.useState(-1)
  const stepsAmount = steps || 10
  const firstColor = color1 || '#ffffff'
  const secondColor = color2 || '000000'

  React.useEffect(() => {
    let dataHighestValue = 0
    for (let r = 0; r < rows.length; r++) {
      const currentRow = rows[r]
      let rowHighest = 0

      for (let c = 0; c < currentRow.data.length; c++) {
        const value1 = currentRow.data[c].item1.value
          ? (currentRow.data[c].item1.value as number)
          : 0
        const value2 = currentRow.data[c].item2.value
          ? (currentRow.data[c].item2.value as number)
          : 0

        const currentCellValue = value1 + value2
        if (currentCellValue > rowHighest) rowHighest = currentCellValue
      }
      if (rowHighest > dataHighestValue) dataHighestValue = rowHighest
    }

    setHighestValue(dataHighestValue)
  }, [highestValue])

  /**
   * Turns two hex colors to a lerp of rgb values
   * @param color1 first color in hex
   * @param color2 second color in hex
   * @param steps amount of steps
   * @returns an array of objects containing rgb values
   */
  const interpolateHexColorsToRGB = (
    color1: string,
    color2: string,
    steps: number
  ) => {
    // Parse the hex colors into RGB values
    const c1R = parseInt(color1.slice(1, 3), 16)
    const c1G = parseInt(color1.slice(3, 5), 16)
    const c1B = parseInt(color1.slice(5, 7), 16)

    const c2R = parseInt(color2.slice(1, 3), 16)
    const c2G = parseInt(color2.slice(3, 5), 16)
    const c2B = parseInt(color2.slice(5, 7), 16)

    // Calculate the difference between the two colors
    const diffR = c2R - c1R
    const diffG = c2G - c1G
    const diffB = c2B - c1B

    const interpolatedColors = []

    // Create the interpolated array of colors
    for (let i = 0; i < steps; i++) {
      const ratio = i / (steps - 1)
      const r = Math.round(c1R + ratio * diffR)
      const g = Math.round(c1G + ratio * diffG)
      const b = Math.round(c1B + ratio * diffB)

      interpolatedColors.push({ red: r, green: g, blue: b })
    }

    return interpolatedColors
  }

  const rgbColors = interpolateHexColorsToRGB(
    firstColor,
    secondColor,
    stepsAmount
  )

  return (
    <div
      className={`duo-heatmap ${modifier ? 'duo-heatmap--' + modifier : ''}`}
    >
      {rows.map((row, index) => (
        <Row label={row.label} index={index} key={row.label + index}>
          {highestValue === -1
            ? null
            : row.data.map((cell) => (
                <Cell
                  key={cell.id}
                  highestValue={highestValue}
                  colors={rgbColors}
                  item1={cell.item1}
                  item2={cell.item2}
                />
              ))}
        </Row>
      ))}
    </div>
  )
}

export default DuoHeatmap
