import './row-styles.scss'
import React from 'react'
import { ICell } from '../cell/cell-component'

export interface IRow {
  label: string
  data: ICell[]
}

interface Props {
  label: string
  index: number
}

const Row: React.FC<Props> = (props) => {
  const { label, children, index } = props

  return (
    <div className={`row row--${index}`}>
      <div className='row__label'>{label}</div>
      <div className='row__data'>{children}</div>
    </div>
  )
}

export default Row
import React from 'react'

export interface Item {
  label: string
  value: number
}

export interface ICell {
  id: number
  item1: Item
  item2: Item
}

export interface Props {
  colors: string[]
  highestValue: number
  item1: Item
  item2: Item
}

const Cell: React.FC<Props> = (props) => {
  const { item1, item2, colors, highestValue } = props
  const maxValue = item1.value + item2.value
  const ratio = 1 - item1.value / maxValue
  const colorIndex = ratio < 0.05 ? 0 : Math.round(ratio * 10) - 1
  const cellColor = colors[colorIndex]
  const maxValueRatio = maxValue / highestValue

  return (
    <div
      className='cell'
      style={{ backgroundColor: cellColor, opacity: maxValueRatio }}
    >
      <div className='cell__data'>
        <div className='cell__item'>
          <span className='cell__item--label'>{item1.label}</span>
          <span className='cell__item--value'>{item1.value}</span>
        </div>
        <div className='cell__item'>
          <span className='cell__item--label'>{item2.label}</span>
          <span className='cell__item--value'>{item2.value}</span>
        </div>
      </div>
    </div>
  )
}

export default Cell
import * as React from 'react'
import './styles.module.scss'
import Row, { IRow } from './components/row/row-component'
import Cell from './components/cell/cell-component'

interface Props {
  rows: IRow[]
  color1: string
  color2: string
  modifier?: string
}

export const DuoHeatmap: React.FC<Props> = (props) => {
  const { modifier, rows, color1, color2 } = props
  const [highestValue, setHighestValue] = React.useState(0)

  React.useEffect(() => {
    let dataHighestValue = 0
    for (let r = 0; r < rows.length; r++) {
      const currentRow = rows[r]
      let rowHighest = 0
      for (let c = 0; c < currentRow.data.length; c++) {
        const currentCellValue =
          currentRow.data[c].item1.value + currentRow.data[c].item2.value / 2
        if (currentCellValue > rowHighest) rowHighest = currentCellValue
      }
      if (rowHighest > dataHighestValue) dataHighestValue = rowHighest
    }

    setHighestValue(dataHighestValue)
  }, [highestValue])

  const interpolateHexColors = (
    color1: string,
    color2: string,
    steps: number
  ) => {
    // Parse the hex colors into RGB values
    const c1R = parseInt(color1.slice(1, 3), 16)
    const c1G = parseInt(color1.slice(3, 5), 16)
    const c1B = parseInt(color1.slice(5, 7), 16)

    const c2R = parseInt(color2.slice(1, 3), 16)
    const c2G = parseInt(color2.slice(3, 5), 16)
    const c2B = parseInt(color2.slice(5, 7), 16)

    // Calculate the difference between the two colors
    const diffR = c2R - c1R
    const diffG = c2G - c1G
    const diffB = c2B - c1B

    const interpolatedColors = []
    // Create the interpolated array of colors
    for (let i = 0; i < steps; i++) {
      if (i === 0) {
        interpolatedColors.push(color1)
      } else if (i === steps - 1) {
        interpolatedColors.push(color2)
      } else {
        const ratio = i / (steps - 1)
        const r = Math.round(c1R + ratio * diffR)
        const g = Math.round(c1G + ratio * diffG)
        const b = Math.round(c1B + ratio * diffB)

        let rHex = r.toString(16)
        let gHex = g.toString(16)
        let bHex = b.toString(16)

        if (rHex.length === 1) {
          rHex = '0' + rHex
        }
        if (gHex.length === 1) {
          gHex = '0' + gHex
        }
        if (bHex.length === 1) {
          bHex = '0' + bHex
        }

        interpolatedColors.push(`#${rHex}${gHex}${bHex}`)
      }
    }

    return interpolatedColors
  }

  const colors = interpolateHexColors(color1, color2, 10)

  return (
    <div>
      <div
        className={`duo-heatmap ${modifier ? 'duo-heatmap--' + modifier : ''}`}
      >
        {rows.map((row, index) => (
          <Row label={row.label} index={index} key={row.label + index}>
            {row.data.map((cell) => (
              <Cell
                key={cell.id}
                highestValue={highestValue}
                colors={colors}
                item1={cell.item1}
                item2={cell.item2}
              />
            ))}
          </Row>
        ))}
      </div>
    </div>
  )
}
